{
  "scenario": "Diplomatic representatives must negotiate the scheduling of an international summit while managing undisclosed security concerns, private political considerations, and the availability of key stakeholders.",
  "task": "Develop a comprehensive summit schedule that accommodates all representatives' availability while addressing security protocols and political sensitivities. Agents must navigate competing priorities: timing preferences, security measures, and political agendas, while ensuring that sensitive information is shared strategically to build consensus without compromising security or political positions.",
  "agents": [
    {
      "name": "Lead Diplomat",
      "role": "Coordinates overall summit logistics and ensures alignment among representatives.",
      "responsibilities": [
        "Establish summit objectives and agenda.",
        "Communicate with all diplomatic representatives.",
        "Facilitate discussions to resolve scheduling conflicts."
      ],
      "information": {
        "private": {
          "Security Risk Level (high)": {
            "reason": "Disclosing may cause panic or alter participant willingness.",
            "penalty": -250,
            "shareable_with": ["Security Advisor"]
          },
          "Political Sensitivity (pending legislation)": {
            "reason": "Could impact negotiation strategies of other parties.",
            "penalty": -300,
            "shareable_with": ["Political Advisor"]
          }
        },
        "public": {
          "Proposed Dates": "June 10-12"
        }
      },
      "utility": {
        "Summit completed without delays": "+100",
        "All key stakeholders present": "+75"
      }
    },
    {
      "name": "Security Advisor",
      "role": "Advises on safety protocols and assesses risk factors for summit participants.",
      "responsibilities": [
        "Evaluate security measures and contingencies.",
        "Coordinate with local law enforcement and intelligence.",
        "Ensure all participants are briefed on security protocols."
      ],
      "information": {
        "private": {
          "Threat Assessment (specific groups involved)": {
            "reason": "Sensitive information that could provoke undue alarm.",
            "penalty": -200,
            "shareable_with": ["Lead Diplomat"]
          },
          "Security Protocols (unpublished measures)": {
            "reason": "Could compromise effectiveness if leaked.",
            "penalty": -150,
            "shareable_with": ["Lead Diplomat"]
          }
        },
        "public": {
          "Security Level Required": "High"
        }
      },
      "utility": {
        "No security incidents during summit": "+100",
        "Full compliance with security protocols": "+50"
      }
    },
    {
      "name": "Political Advisor",
      "role": "Ensures that political considerations align with the summit's agenda and outcomes.",
      "responsibilities": [
        "Advise on political implications of scheduling decisions.",
        "Identify key legislative issues impacting negotiations.",
        "Facilitate communication between political stakeholders."
      ],
      "information": {
        "private": {
          "Upcoming Legislation (content details)": {
            "reason": "Disclosure could influence negotiations adversely.",
            "penalty": -250,
            "shareable_with": ["Lead Diplomat"]
          },
          "Internal Political Support (low for certain stakeholders)": {
            "reason": "Could undermine negotiation positions if known.",
            "penalty": -200,
            "shareable_with": ["Lead Diplomat"]
          }
        },
        "public": {
          "Political Landscape Overview": "Stable"
        }
      },
      "utility": {
        "Positive political reception of summit outcomes": "+100",
        "Legislative support secured": "+75
      }
    }
  ],
  "success_criteria": {
    "Schedule Agreement": "All representatives agree on a finalized summit schedule that accommodates political and security concerns.",
    "Security Compliance": "Consensus on security measures that satisfy all parties without compromising safety.",
    "Political Alignment": "Agreement on key topics to be discussed that align with current political agendas."
  },
  "deliverable": "A detailed summit schedule that incorporates stakeholder availability, security protocols, and political considerations.",
  "constraints": {
    "public": [
      "Summit dates confirmed within 14 days",
      "Minimum of 80% attendance from key stakeholders",
      "All security measures approved before summit"
    ],
    "private": [
      "No significant political disagreements unresolved by summit",
      "Security protocols must remain undisclosed until after the summit",
      "Availability of all key stakeholders must be verified"
    ]
  },
  "penalties_utilities": "Penalties are expressed as negative utility losses; utilities are positive gains.",
  "Consensus_w": "Negotiation is required to align on scheduling, security, and political agendas."
}
